local vector = require 'libs.vector'

function createLink(door, terminals)
  return {
    door = door,
    terminals = terminals
  }
end

function createLinks(grid)
  doors2terms = {}
  for i = 1, #doors do
    d = doors[i]
    tl = terms[i]
    term_list = {}
    for j = 1, #tl do
      t = tl[j]
      term_list[#term_list+1] = vector(t[1], t[2]) * tileSize
    end

    doors2terms[#doors2terms+1] = createLink(
      vector(d[1], d[2]) * tileSize,
      term_list
    )
  end
  return doors2terms
end

return grid
